#include "gtest/gtest.h"

#include <vector>
#include <string>
#include <sstream>
#include <fstream>
#include <iostream>
#include <dirent.h>

#include "mesh.h"

using namespace std;
using namespace simit;

TEST(Mesh, MeshlabTest) {
  const string input = R"(####
#
# OBJ File Generated by Meshlab
#
####
# Object cubeb.obj
#
# Vertices: 8
# Faces: 12
#
####
v 0.000000 0.000000 0.000000
v 0.010000 0.000000 0.000000
v 0.010000 0.000000 0.010000
v 0.000000 0.000000 0.010000
v 0.000000 0.100000 0.000000
v 0.010000 0.100000 0.000000
v 0.010000 0.100000 0.010000
v 0.000000 0.100000 0.010000
# 8 vertices, 0 vertices normals

f 1 2 4
f 2 3 4
f 6 1 5
f 6 2 1
f 7 2 6
f 7 3 2
f 4 3 7
f 4 7 8
f 5 1 4
f 4 8 5
f 8 6 5
f 8 7 6
# 12 faces, 0 coords texture

# End of File)";

  const string output = R"(v 0 0 0
v 0.01 0 0
v 0.01 0 0.01
v 0 0 0.01
v 0 0.1 0
v 0.01 0.1 0
v 0.01 0.1 0.01
v 0 0.1 0.01
f 1 2 4
f 2 3 4
f 6 1 5
f 6 2 1
f 7 2 6
f 7 3 2
f 4 3 7
f 4 7 8
f 5 1 4
f 4 8 5
f 8 6 5
f 8 7 6
#end
)";
    Mesh m;
    stringstream inputstream, outputstream;
    inputstream << input;
    m.load(inputstream);
    m.save(outputstream);
    string outputstring;
    getline(outputstream, outputstring, '\0');
    ASSERT_STREQ(&(output[0]), &(outputstring[0]));
}

